CREATE TABLE USERS (USERID NUMERIC(22,0) NOT NULL, USERNAME VARCHAR(20), PASSWORD VARCHAR(20), FIRST_NAME VARCHAR(20), LAST_NAME VARCHAR(20), MIDDLE_NAME VARCHAR(20), COMPANYNAME VARCHAR(100), VENDOR_ID NUMERIC(22,0), VENDOR_EXP_DATE DATETIME, ACTIVE CHAR(1), EMAIL_ID VARCHAR(50), PHONE VARCHAR(15), CREATED_BY NUMERIC(18,0), CREATED DATETIME, UPDATED_BY NUMERIC(18,0), UPDATED DATETIME, PRIMARY KEY (USERID));
