CREATE TABLE MAPPING_FIELDS (ID INT NOT NULL IDENTITY, NAME VARCHAR(250), DESCRIPTION VARCHAR(500), SOURCE_SCHEMA_NAME VARCHAR(200), SOURCE_TABLE_NAME VARCHAR(250), SOURCE_FIELDS VARCHAR(4000), TARGET_SCHEMA_NAME VARCHAR(200), TARGET_TABLE_NAME VARCHAR(250), TARGET_FIELDS VARCHAR(4000), CREATED_BY NUMERIC(18,0), CREATED DATETIME, UPDATED_BY NUMERIC(18,0), UPDATED DATETIME, PRIMARY KEY (ID));

CREATE TABLE JOBS (ID INT NOT NULL IDENTITY, MAPPING_ID INT, NAME VARCHAR(200), DESCRIPTION VARCHAR(500), TOTAL_ROWS NUMERIC(18,0), TOTAL_TIME NUMERIC(18,0), PENDING_ROWS NUMERIC(18,0), PENDING_TIME NUMERIC(18,0), CREATED_BY NUMERIC(18,0), CREATED DATETIME, UPDATED_BY NUMERIC(18,0), UPDATED DATETIME, PRIMARY KEY (ID));

CREATE TABLE DB_CONNECTION (ID INT NOT NULL IDENTITY, DATABASE_TYPE INT, NAME VARCHAR(200) NOT NULL, DESCRIPTION VARCHAR(500), URL VARCHAR(500),DRIVER_CLASS VARCHAR(500),USERNAME VARCHAR(500),PASSWORD VARCHAR(500), CREATED_BY NUMERIC(18,0), CREATED DATETIME, UPDATED_BY NUMERIC(18,0), UPDATED DATETIME, PRIMARY KEY (ID));
